<script setup>
const mobilePanel = ref(false)
const toggleMobilePanel = () => {
  mobilePanel.value = !mobilePanel.value
  document.body.classList.toggle('overflow-hidden')
}

const menuOpen = ref(false)


const toggleMenu = () => {
  menuOpen.value = !menuOpen.value
  document.body.classList.toggle('overflow-hidden')
}

const newUser = ref(false)
const logged = ref(false)
const downloadClient = ref(false)

let loading = ref(false)

const toggleLogged = () => {
  loading.value = true
  setTimeout(() => {
    logged.value = !logged.value;
    loading.value = false
  }, 2000);
};
const createAcc = () => {
  newUser.value = !newUser.value;
};

const showDownloadClient = () => {
  downloadClient.value = !downloadClient.value;
};

const scrollToTop = () => {
  window.scrollTo(0,0);
}

//////////// Mobile ////////////

const isSmallWindow = ref(false);

// Function to check window width and update the variable
const updateWindowWidth = () => {
  isSmallWindow.value = window.innerWidth < 1024;
};

// Call the function once when the component is mounted
onMounted(() => {
  updateWindowWidth();
  window.addEventListener('resize', updateWindowWidth);
});

// Remove the event listener when the component is unmounted
onBeforeUnmount(() => {
  window.removeEventListener('resize', updateWindowWidth);
});
</script>